(1, '{\"en\":\"Ultimate Wellness & Beauty Experience\"}', '100.00', '{\"en\":\"Indulge in our Ultimate Wellness & Beauty Experience, a luxurious combination of our top-rated Pain Relief Therapy and Natural Skincare Treatment. This exclusive package is designed to rejuvenate your body and skin, providing a holistic approach to wellness and beauty.\"}', NULL, '5.00', 1, 1, '#53a3d5', 'primary', 'ultimate-wellness-beauty-experience', NULL, NULL, 30, 1, '2024-06-15', '2024-06-30', NULL, '2024-06-15 11:49:20', '2024-06-15 11:49:20'),
(2, '{\"en\":\"Gourmet Dining Experience\"}', '1500.00', '{\"en\":\"Elevate your dining with our Gourmet Dining Experience, a premium package that seamlessly blends our exceptional Catering Services and Personal Chef Service. This unique offering is perfect for any occasion, whether it\'s an intimate dinner party, a family gathering, or a grand celebration.\"}', NULL, '1.00', 1, 1, '#0f84cc', 'secondary', 'gourmet-dining-experience', NULL, NULL, 23, 1, '2024-06-15', '2024-06-30', NULL, '2024-06-15 11:57:26', '2024-06-15 11:57:26'),
(3, '{\"en\":\"Home Transformation Package\"}', '5500.00', '{\"en\":\"Revitalize your living space with our Home Transformation Package, a comprehensive service that combines expert Electrical Wiring and Installation with professional Interior House Painting. This package is designed to enhance both the functionality and aesthetic appeal of your home, creating a safe, beautiful, and modern environment.\"}', NULL, '2.00', 1, 1, '#5715d1', 'info', 'home-transformation-package', NULL, NULL, 31, 1, '2024-06-15', '2024-06-30', NULL, '2024-06-15 12:09:53', '2024-06-15 12:09:53'),
(4, '{\"en\":\"Holistic Ayurvedic Healing\"}', '650.00', '{\"en\":\"Immerse yourself in the ancient wisdom of Ayurveda with our Holistic Ayurvedic Healing package, which combines our specialized Ayurvedic Pain Relief Head, Neck & Shoulder Massage with a full-body Ayurvedic Massage. This comprehensive service is designed to provide deep relaxation, pain relief, and overall wellness, balancing your body and mind through time-honored techniques.\"}', NULL, '10.00', 1, 1, '#7f25bb', 'success', 'holistic-ayurvedic-healing', NULL, NULL, 33, 1, '2024-06-15', '2024-06-30', NULL, '2024-06-15 12:31:28', '2024-06-15 12:31:28'),
(5, '{\"en\":\"Men\'s Ultimate Rejuvenation\"}', '1200.00', '{\"en\":\"Discover the pinnacle of relaxation and skincare with our Men\'s Ultimate Rejuvenation package. This exclusive offering combines our specialized Face Care & Facial Detan Service for Men with a revitalizing Full Body Massage, designed to refresh, rejuvenate, and renew your body and skin.\"}', NULL, '1.00', 1, 1, '#122c97', 'warning', 'men-s-ultimate-rejuvenation', NULL, NULL, 33, 1, '2024-06-15', '2024-06-30', NULL, '2024-06-15 12:33:54', '2024-06-15 12:33:54'),
(6, '{\"en\":\"Comprehensive Clean Spaces\"}', '800.00', '{\"en\":\"Achieve the highest standards of cleanliness with our Comprehensive Clean Spaces package, a dual-service offering that combines our meticulous Bathroom & Kitchen Deep Cleaning with our thorough Office Cleaning Service. This package is ideal for those who seek a pristine and hygienic environment both at home and at work.\"}', NULL, '10.00', 1, 1, '#9b159d', 'danger', 'comprehensive-clean-spaces', NULL, NULL, 3, 1, '2024-06-15', '2024-06-30', NULL, '2024-06-15 12:36:34', '2024-06-19 09:12:29'),
(7, '{\"en\":\"Air Conditioner Repair & Maintenance:\"}', '3598.00', '{\"en\":\"AC inspection, cleaning, diagnostics, minor repairs, and cooling performance optimization. We ensure that your air conditioning system is operating efficiently by providing maintenance and addressing any issues like leaks, low coolant levels, or poor performance. This package also includes a full inspection to catch any potential future problems.\"}', NULL, '20.00', 1, 0, '#00ebcf', 'primary', 'air-conditioner-repair-maintenance', NULL, NULL, 3, 1, '2024-11-15', '2024-11-28', NULL, '2024-11-11 12:59:15', '2024-11-12 14:08:23'),
(8, '{\"en\":\"Electrical Troubleshooting & Repairs:\"}', '2698.00', '{\"en\":\"Electrical troubleshooting for problems such as faulty wiring, tripped breakers, and malfunctioning outlets. Our licensed electricians will help identify and fix electrical issues in your home, ensuring the safety of your electrical system. We provide repairs and safety inspections to keep your home up to code.\"}', NULL, '17.00', 1, 0, '#b80096', 'danger', 'electrical-troubleshooting-repairs', NULL, NULL, 3, 1, '2024-11-21', '2024-11-30', NULL, '2024-11-11 13:01:16', '2024-11-12 14:07:37'),
(9, '{\"en\":\"Plumbing Repairs & Leak Fixing\"}', '3499.00', '{\"en\":\"Fixing leaky pipes, clogged drains, and other common plumbing issues. We quickly identify and repair plumbing issues in your home. Whether it\\u00e2\\u20ac\\u2122s a leaking faucet, clogged sink, or broken pipe, our plumbers will fix it efficiently, preventing water damage and potential expensive repairs.\"}', NULL, '20.00', 1, 0, '#000000', 'info', 'plumbing-repairs-leak-fixing', NULL, NULL, 22, 1, '2024-11-22', '2024-11-30', NULL, '2024-11-11 13:02:35', '2024-11-12 14:05:32');
